// TodoListHeader.js
import React, { useState } from "react";

const TodoListHeader = ({ toggleShowNotFinished }) => {
  const [showNotFinishedOnly, setShowNotFinishedOnly] = useState(false);

  const handleCheckboxChange = () => {
    setShowNotFinishedOnly(!showNotFinishedOnly);
    toggleShowNotFinished(!showNotFinishedOnly);
  };

  return (
    <div className="header">
      <input
        type="checkbox"
        checked={showNotFinishedOnly}
        onChange={handleCheckboxChange}
      />
      <span>Show Not Finished Only</span>
    </div>
  );
};

export default TodoListHeader;
