// Modify TodoList.js

import React, { useState } from "react";
import { FaRegCircle, FaRegCheckCircle } from "react-icons/fa";

const TodoList = ({ tasks, setTasks, showNotFinishedOnly }) => {
  const [draggedTask, setDraggedTask] = useState(null);

  const toggleDone = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, done: !task.done } : task
      )
    );
  };

  const handleDragStart = (task) => {
    setDraggedTask(task);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = () => {
    setTasks((prevTasks) => {
      const updatedTasks = prevTasks.filter(
        (task) => task.id !== draggedTask.id
      );
      const dropIndex = tasks.findIndex((task) => task.id === draggedTask.id);
      updatedTasks.splice(dropIndex, 0, draggedTask);
      return updatedTasks;
    });
    setDraggedTask(null);
  };

  const filteredTasks = showNotFinishedOnly
    ? tasks.filter((task) => !task.done)
    : tasks;

  return (
    <div className="todo-list-container">
      {filteredTasks.map((task) => (
        <div
          key={task.id}
          className={`todo-item-container ${task.done ? "done" : ""}`}
          draggable
          onDragStart={() => handleDragStart(task)}
          onDragOver={(e) => handleDragOver(e)}
          onDrop={() => handleDrop()}
        >
          {task.done ? (
            <FaRegCheckCircle
              className="item-done-button"
              color="#9a9a9a"
              onClick={() => toggleDone(task.id)}
            />
          ) : (
            <FaRegCircle
              className="item-done-button"
              color="#9a9a9a"
              onClick={() => toggleDone(task.id)}
            />
          )}
          <div className="item-title">{task.title}</div>
          {task.dueDate && (
            <div className="item-due-date">
              {calculateDaysLeft(task.dueDate)} days left
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default TodoList;
