// TodoListHeader.js
import React, { useState } from "react";

const TodoListHeader = ({ undoneTasks, onFilterChange }) => {
  const [showNotFinishedOnly, setShowNotFinishedOnly] = useState(false);

  const handleFilterChange = () => {
    setShowNotFinishedOnly(!showNotFinishedOnly);
    onFilterChange(!showNotFinishedOnly);
  };

  return (
    <div className="header">
      <label>
        <input
          type="checkbox"
          checked={showNotFinishedOnly}
          onChange={handleFilterChange}
        />
        Not finished only
      </label>
      <span>You have {undoneTasks} tasks left!</span>
    </div>
  );
};

export default TodoListHeader;
