// TodoList.js
import React, { useState, useEffect } from "react";
import { FaRegCircle, FaRegCheckCircle } from "react-icons/fa";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const TodoList = ({
  initialTasks,
  showNotFinishedOnly,
  toggleShowNotFinished,
}) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const storedTasks =
      JSON.parse(localStorage.getItem("tasks")) || initialTasks;
    setTasks(storedTasks);
  }, [initialTasks]);

  const addTask = (title, dueDate) => {
    const newTask = { title, done: false, dueDate };
    setTasks([...tasks, newTask]);
    localStorage.setItem("tasks", JSON.stringify([...tasks, newTask]));
  };

  const toggleTaskStatus = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].done = !updatedTasks[index].done;
    setTasks(updatedTasks);
    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const updatedTasks = [...tasks];
    const [reorderedItem] = updatedTasks.splice(result.source.index, 1);
    updatedTasks.splice(result.destination.index, 0, reorderedItem);

    setTasks(updatedTasks);
    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
  };

  const renderTasks = (showNotFinishedOnly) => {
    const filteredTasks = showNotFinishedOnly
      ? tasks.filter((task) => !task.done)
      : tasks;

    return filteredTasks.map((task, index) => (
      <Draggable key={index} draggableId={`task-${index}`} index={index}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            className={`todo-item-container ${task.done ? "done" : ""}`}
          >
            {task.done ? (
              <FaRegCheckCircle
                color="#9a9a9a"
                className="item-done-button"
                onClick={() => toggleTaskStatus(index)}
              />
            ) : (
              <FaRegCircle
                color="#9a9a9a"
                className="item-done-button"
                onClick={() => toggleTaskStatus(index)}
              />
            )}
            <div className="item-title">{task.title}</div>
            {task.dueDate && (
              <div className="due-date">
                Due in {calculateDaysLeft(task.dueDate)} days
              </div>
            )}
          </div>
        )}
      </Draggable>
    ));
  };

  const calculateDaysLeft = (dueDate) => {
    const today = new Date();
    const due = new Date(dueDate);
    const timeDiff = due.getTime() - today.getTime();
    return Math.ceil(timeDiff / (1000 * 3600 * 24));
  };

  return (
    <div className="todo-list-container">{/* ... other components */}</div>
  );
};

export default TodoList;
